// frontend/src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';

// -------------------------------------------------
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
// -------------------------------------------------

const CustomLoadingSpinner = () => (
  <div className="flex justify-center items-center space-x-2">
    <div className="w-2 h-2 bg-purple-500 rounded-full animate-bounce"></div>
    <div className="w-2 h-2 bg-purple-500 rounded-full animate-bounce delay-100"></div>
    <div className="w-2 h-2 bg-purple-500 rounded-full animate-bounce delay-200"></div>
    <span className="text-purple-400 text-sm">جارٍ التحميل...</span>
  </div>
);

const Login = () => {
  // تحميل البيانات المحفوظة من localStorage
  const loadSavedData = () => {
    const savedLogin = localStorage.getItem('loginForm');
    const savedRegister = localStorage.getItem('registerForm');

    return {
      login: savedLogin ? JSON.parse(savedLogin) : { emailOrPhone: '', password: '' },
      register: savedRegister ? JSON.parse(savedRegister) : { name: '', phone: '', password: '' }
    };
  };

  const { login: savedLogin, register: savedRegister } = loadSavedData();

  const [form, setForm] = useState(savedLogin);
  const [registerForm, setRegisterForm] = useState(savedRegister);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showIntro, setShowIntro] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const navigate = useNavigate();

  // حفظ نموذج تسجيل الدخول في localStorage
  useEffect(() => {
    localStorage.setItem('loginForm', JSON.stringify(form));
  }, [form]);

  // حفظ نموذج التسجيل في localStorage
  useEffect(() => {
    localStorage.setItem('registerForm', JSON.stringify(registerForm));
  }, [registerForm]);

  // أنيميشن الإنترو
  useEffect(() => {
    const t1 = setTimeout(() => {}, 100);
    const t2 = setTimeout(() => {}, 600);
    const t3 = setTimeout(() => setShowIntro(false), 2000);
    const t4 = setTimeout(() => setShowForm(true), 2200);
    return () => {
      clearTimeout(t1);
      clearTimeout(t2);
      clearTimeout(t3);
      clearTimeout(t4);
    };
  }, []);

  // إشعار واتساب كل 5 ثواني
  useEffect(() => {
    const messages = [
      'للشكاوي تواصل معانا',
      'لتفعيل الحساب تواصل معانا'
    ];
    let index = 0;
    const showToast = () => {
      setToastMessage(messages[index]);
      index = (index + 1) % messages.length;
      setTimeout(() => setToastMessage(''), 3000);
    };
    const interval = setInterval(showToast, 5000);
    return () => clearInterval(interval);
  }, []);

  // ---------- تسجيل الدخول ----------
  const handleLogin = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const payload = {};
      if (/^\d{11}$/.test(form.emailOrPhone)) {
        payload.phone = form.emailOrPhone;
      } else {
        payload.email = form.emailOrPhone;
      }
      payload.password = form.password;

      const res = await axios.post(`${API_URL}/api/auth/login`, payload);

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('role', res.data.role);
      localStorage.setItem('userId', res.data.userId);
      window.dispatchEvent(new Event('authChange'));

      setTimeout(() => {
        const { role } = res.data;
        if (role === 'admin') navigate('/admin');
        else if (role === 'vendor') navigate('/vendor');
        else navigate('/');
      }, 600);
    } catch (err) {
      setError(err.response?.data?.message || 'حدث خطأ، حاول مرة أخرى');
    } finally {
      setLoading(false);
    }
  };

  // ---------- إنشاء حساب ----------
  const handleRegister = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const payload = {
        name: registerForm.name,
        phone: registerForm.phone,
        password: registerForm.password,
      };

      await axios.post(`${API_URL}/api/auth/register-customer-public`, payload);
      setSuccess('تم إرسال طلب التسجيل بنجاح! بانتظار موافقة الأدمن');
      setRegisterForm({ name: '', phone: '', password: '' });
      localStorage.removeItem('registerForm');
      setTimeout(() => setIsRegistering(false), 2000);
    } catch (err) {
      setError(err.response?.data?.message || 'فشل في إنشاء الحساب');
    } finally {
      setLoading(false);
    }
  };

  // فتح واتساب
  const openWhatsApp = () => {
    const phone = '201553531373';
    const url = `https://wa.me/${phone}`;
    window.open(url, '_blank', 'noopener,noreferrer');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#18191a] via-[#1f2023] to-[#242526] flex items-center justify-center p-4 relative overflow-hidden">
      {/* خلفية ناعمة */}
      <div className="absolute inset-0 bg-gradient-to-t from-purple-900/20 to-transparent pointer-events-none"></div>
      <div className="absolute top-20 left-20 w-72 h-72 bg-purple-600/30 rounded-full filter blur-3xl animate-pulse"></div>
      <div className="absolute bottom-20 right-20 w-96 h-96 bg-pink-600/20 rounded-full filter blur-3xl animate-pulse delay-1000"></div>

      {/* إنترو */}
      <AnimatePresence>
        {showIntro && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="absolute inset-0 flex flex-col items-center justify-center z-50"
          >
            <div className="relative w-32 h-32 mb-8">
              <div className="absolute inset-0 rounded-full bg-gradient-to-r from-purple-600 to-pink-600 animate-spin"></div>
              <div className="absolute inset-2 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 animate-spin delay-200"></div>
              <div className="absolute inset-4 rounded-full bg-gradient-to-r from-purple-400 to-pink-400 animate-spin delay-300"></div>
              <div className="absolute inset-0 flex items-center justify-center text-white font-bold text-2xl">
                S
              </div>
            </div>

            <motion.h1
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.5 }}
              className="text-4xl md:text-5xl font-bold text-white mb-2"
            >
              أهلاً بك في
            </motion.h1>
            <motion.p
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.7 }}
              className="text-2xl md:text-3xl text-purple-300 font-medium"
            >
              Web Shose
            </motion.p>
          </motion.div>
        )}
      </AnimatePresence>

      {/* نموذج الدخول / التسجيل */}
      <AnimatePresence>
        {showForm && (
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: 'easeOut' }}
            className="relative z-10 w-full max-w-md"
          >
            {/* تبويب */}
            <div className="flex bg-gray-800/50 backdrop-blur-sm rounded-t-2xl overflow-hidden mb-1">
              <button
                onClick={() => setIsRegistering(false)}
                className={`px-4 py-2 rounded-lg font-medium transition ${
                  !isRegistering ? 'bg-purple-700 text-white shadow-md' : 'bg-gray-700 text-gray-400'
                }`}
              >
                تسجيل الدخول
              </button>
              <button
                onClick={() => setIsRegistering(true)}
                className={`px-4 py-2 rounded-lg font-medium transition ${
                  isRegistering ? 'bg-purple-700 text-white shadow-md' : 'bg-gray-700 text-gray-400'
                }`}
              >
                إنشاء حساب
              </button>
            </div>

            {/* رسائل */}
            <div className="bg-gray-800/50 backdrop-blur-sm p-6 rounded-b-2xl shadow-2xl border border-gray-700/50">
              {error && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mb-4 p-3 bg-red-900/50 border border-red-700 text-red-300 rounded-lg text-sm text-center"
                >
                  {error}
                </motion.div>
              )}
              {success && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mb-4 p-3 bg-green-900/50 border border-green-700 text-green-300 rounded-lg text-sm text-center"
                >
                  {success}
                </motion.div>
              )}

              {/* ==== تسجيل الدخول ==== */}
              {!isRegistering ? (
                <form onSubmit={handleLogin} className="space-y-4">
                  <div>
                    <label className="block text-gray-300 text-sm mb-1">البريد أو الهاتف</label>
                    <input
                      type="text"
                      value={form.emailOrPhone}
                      onChange={e => setForm({ ...form, emailOrPhone: e.target.value })}
                      className="w-full p-4 bg-[#3a3b3c]/60 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-sm focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-500/20 transition-all duration-200"
                      placeholder="example@domain.com أو 01234567890"
                      required
                      disabled={loading}
                    />
                  </div>

                  <div>
                    <label className="block text-gray-300 text-sm mb-1">كلمة المرور</label>
                    <input
                      type="password"
                      value={form.password}
                      onChange={e => setForm({ ...form, password: e.target.value })}
                      className="w-full p-4 bg-[#3a3b3c]/60 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-sm focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-500/20 transition-all duration-200"
                      placeholder="••••••••"
                      required
                      disabled={loading}
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={loading}
                    className="w-full py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white font-bold rounded-xl shadow-lg hover:shadow-purple-500/50 transform hover:scale-105 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                  >
                    {loading ? <CustomLoadingSpinner /> : 'دخول'}
                  </button>
                </form>
              ) : (
                /* ==== إنشاء حساب ==== */
                <form onSubmit={handleRegister} className="space-y-4">
                  <div>
                    <label className="block text-gray-300 text-sm mb-1">الاسم الكامل</label>
                    <input
                      type="text"
                      value={registerForm.name}
                      onChange={e => setRegisterForm({ ...registerForm, name: e.target.value })}
                      className="w-full p-4 bg-[#3a3b3c]/60 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-sm focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-500/20 transition-all duration-200"
                      placeholder="أحمد محمد"
                      required
                      disabled={loading}
                    />
                  </div>

                  <div>
                    <label className="block text-gray-300 text-sm mb-1">رقم الهاتف (11 رقم)</label>
                    <input
                      type="text"
                      value={registerForm.phone}
                      onChange={e => {
                        const v = e.target.value.replace(/\D/g, '').slice(0, 11);
                        setRegisterForm({ ...registerForm, phone: v });
                      }}
                      className="w-full p-4 bg-[#3a3b3c]/60 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-sm focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-500/20 transition-all duration-200"
                      placeholder="01234567890"
                      required
                      disabled={loading}
                      maxLength="11"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-300 text-sm mb-1">كلمة المرور</label>
                    <input
                      type="password"
                      value={registerForm.password}
                      onChange={e => setRegisterForm({ ...registerForm, password: e.target.value })}
                      className="w-full p-4 bg-[#3a3b3c]/60 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-sm focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-500/20 transition-all duration-200"
                      placeholder="••••••••"
                      required
                      disabled={loading}
                      minLength="6"
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={loading}
                    className="w-full py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white font-bold rounded-xl shadow-lg hover:shadow-purple-500/50 transform hover:scale-105 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                  >
                    {loading ? <CustomLoadingSpinner /> : 'إنشاء حساب'}
                  </button>
                </form>
              )}

              {/* معلومات مساعدة */}
              <div className="mt-6 text-xs text-gray-400 space-y-1 text-center">
                <p>للأدمن: admin@test.com / 123456</p>
                <p>للتاجر: أنشئ من لوحة الأدمن</p>
                <p>للعملاء: سجل هنا وانتظر موافقة الأدمن</p>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* أيقونة واتساب + إشعار */}
      <div className="fixed bottom-6 right-6 z-40">
        <button
          onClick={openWhatsApp}
          className="relative w-16 h-16 bg-green-500 rounded-full shadow-xl flex items-center justify-center hover:scale-110 transform transition-all duration-200 group"
        >
          <svg
            className="w-9 h-9 text-white"
            fill="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M12.04 2C6.48 2 2 6.48 2 12.04c0 2.24.74 4.3 1.99 5.96l-1.3 4.74 4.86-1.27c1.61 1.17 3.59 1.85 5.71 1.85 5.56 0 10.04-4.48 10.04-10.04S17.6 2 12.04 2zm5.7 14.48c-.15.42-.88.78-1.23.85-.38.07-.88.07-1.51-.23-.8-.38-1.48-.84-2.07-1.42-.59-.58-1.06-1.26-1.37-2.02-.31-.76-.23-1.41.18-1.97.41-.56 1.05-.83 1.69-.74.64.09 1.12.5 1.34.99.22.49.28 1.06.17 1.59-.11.53-.4.97-.81 1.29.41.71.98 1.33 1.67 1.79.69.46 1.47.77 2.3.9.83.13 1.52-.11 1.87-.58.35-.47.48-1.09.37-1.66-.11-.57-.47-.99-.97-1.17-.5-.18-1.1-.12-1.63.15z"/>
          </svg>
        </button>

        {/* إشعار من الأيقونة */}
        {toastMessage && (
          <motion.div
            initial={{ opacity: 0, y: 20, scale: 0.8 }}
            animate={{ opacity: 1, y: -80, scale: 1 }}
            exit={{ opacity: 0, y: -100, scale: 0.8 }}
            className="absolute bottom-20 right-0 bg-gray-900 text-white text-xs px-3 py-2 rounded-lg shadow-xl whitespace-nowrap border border-gray-700"
          >
            {toastMessage}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Login;
